const ADD_CARDS_TO_PONE_DECKS = 'deck/ADD_CARDS_TO_PONE_DECKS';
const ADD_CARDS_TO_PTWO_DECKS = 'deck/ADD_CARDS_TO_PTWO_DECKS';
const ADD_TO_POT = 'pot/ADD_TO_POT';
const GET_POT = 'pot/GET_POT';
const DELETE_PLAYER_CARDS_DISTRIBUTE = 'delete/PLAYER_CARDS';
const DELETE_PLAYERTWO_CARD = 'delete/PLAYERTWO_CARD';

const add_to_pOne_deck = (deck) => {
    return {
        type: ADD_CARDS_TO_PONE_DECKS,
        payload: deck
    }
}

const add_to_pTwo_deck = (deck) => {
    return {
        type: ADD_CARDS_TO_PTWO_DECKS,
        payload: deck
    }
}

const add_to_pot = (pot) => {
    return {
        type: ADD_TO_POT,
        payload: pot
    }
}

const get_pot = (pot) => {
    return {
        type: GET_POT,
        payload: pot
    }
}

const delete_player_card = (deck) => {
    return {
        type: DELETE_PLAYER_CARDS_DISTRIBUTE,
        payload: deck
    }
}

const delete_playerTwo_card = (deck) => {
    return {
        type: DELETE_PLAYERTWO_CARD,
        payload: deck
    }
}

export const get_player_decks = () => async (dispatch) => {

    const response = await fetch('/war/playerDecks')

    if (response.ok) {
        const data = await response.json()
        await dispatch(add_to_pOne_deck(data.playerOneDeck))
        await dispatch(add_to_pTwo_deck(data.playerTwoDeck))
        return data
    }
}



export const addDecksToDatabase = (body) => async (dispatch) => {

    const response = await fetch('/war/start', {
        method: "POST",
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body)
    })

    if (response.ok) {
        const data = await response.json();
        await dispatch(add_to_pOne_deck(data.playerOneDeck))
        await dispatch(add_to_pTwo_deck(data.playerTwoDeck))
        return data
    }

}

export const DeleteAndDistributePlayerCards = (body) => async (dispatch) => {
    const response = await fetch(`/war/deleteCards/`, {
        method: "Delete",
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body)
    })

    if (response.ok) {
        const data = await response.json();
        await dispatch(delete_player_card({ dataOne: data.newPOneDeck, dataTwo: data.newPTwoDeck }))
        // await dispatch(delete_playerTwo_card(data.newPTwoDeck))
        return data
    }
}


export const getPot = (body) => async (dispatch) => {
    const response = await fetch('/war/pot')

    if (response.ok) {
        const data = await response.json()
        await dispatch(get_pot(data.pot))
        return data
    }

}

export const addToPot = (body) => async (dispatch) => {

    const response = await fetch('/war/pot', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body)
    })

    if (response.ok) {
        const data = await response.json()
        await dispatch(add_to_pot(data.pot))
        return data
    }

}



const initialState = { decks: null }

const playerDeckReducer = (state = initialState, action) => {
    let newState;
    switch (action.type) {
        case ADD_CARDS_TO_PONE_DECKS:
            newState = Object.assign({}, state);
            newState.playerOneDeck = action.payload;
            return newState;
        case ADD_CARDS_TO_PTWO_DECKS:
            newState = Object.assign({}, state);
            newState.playerTwoDeck = action.payload;
            return newState;
        case ADD_TO_POT:
            newState = Object.assign({}, state);
            newState.pot = action.payload;
            return newState;
        case GET_POT:
            newState = Object.assign({}, state);
            newState.pot = action.payload;
            return newState;
        case DELETE_PLAYER_CARDS_DISTRIBUTE:
            newState = Object.assign({}, state);
            newState.playerOneDeck = action.payload.dataOne;
            newState.playerTwoDeck = action.payload.dataTwo;
            return newState;
        case DELETE_PLAYERTWO_CARD:
            newState = Object.assign({}, state);
            newState.playerTwoDeck = action.payload;
            return newState;
        default:
            return state;
    }
}

export default playerDeckReducer;
