                                setPhase(Phase.Draw)
                                sessionStorage.setItem('phase', Phase.Draw)
                            }, OPERATIONSPEED)
                            return () => clearTimeout(timeout)
                        }
                    }
                    if (Number(winner) === 2) {
                        console.log('PLAYER TWO WON')
                        const data = await dispatch(playerDecks.DeleteAndDistributePlayerCards({ winner: Number(winner) }))
                        if (data) {
                            const timeout = setTimeout(() => {
                                setPhase(Phase.Draw)
                                sessionStorage.setItem('phase', Phase.Draw)
                            }, OPERATIONSPEED)
                            return () => clearTimeout(timeout)
                        }
                    }
                } else {
                    console.log("WAR")
                    if (Number(winner) === 1) {
                        console.log('PLAYER ONE WON WAR')
                        const data = await dispatch(playerDecks.DeleteAndDistributePlayerCards({ winner: Number(winner) }))
                        if (data) {
                            const timeout = setTimeout(() => {
                                setPhase(Phase.Draw)
                                sessionStorage.setItem('phase', Phase.Draw)
                            }, OPERATIONSPEED)
                            return () => clearTimeout(timeout)
                        }
                    }
                    if (Number(winner) === 2) {
                        console.log('PLAYER TWO WON WAR')
                        const data = await dispatch(playerDecks.DeleteAndDistributePlayerCards({ winner: Number(winner) }))
                        if (data) {
                            const timeout = setTimeout(() => {
                                setPhase(Phase.Draw)
                                sessionStorage.setItem('phase', Phase.Draw)
                            }, OPERATIONSPEED)
                            return () => clearTimeout(timeout)
                        }
                    }
                }
                return;
            case Phase.End:
                console.log("REACHED END PHASE")
                return;
        }

    }, [dispatch, renderStartBtn, phase])

    return (
