const ADD_CARDS_TO_PONE_DECKS = 'deck/ADD_CARDS_TO_PONE_DECKS';
const ADD_CARDS_TO_PTWO_DECKS = 'deck/ADD_CARDS_TO_PTWO_DECKS';
const DISTRIBUTED_PONE_CARDS = 'deck/DISTRIBUTED_PONE_CARDS'
const DISTRIBUTED_PTWO_CARDS = 'deck/DISTRIBUTED_PTWO_CARDS'


const add_to_pOne_deck = (deck) => {
    return {
        type: ADD_CARDS_TO_PONE_DECKS,
        payload: deck
    }
}

const add_to_pTwo_deck = (deck) => {
    return {
        type: ADD_CARDS_TO_PTWO_DECKS,
        payload: deck
    }
}

const new_player_one_deck = (deck) => {
    return {
        type: DISTRIBUTED_PONE_CARDS,
        payload: deck
    }
}

const new_player_two_deck = (deck) => {
    return {
        type: DISTRIBUTED_PTWO_CARDS,
        payload: deck
    }
}

export const get_player_decks = () => async (dispatch) => {

    const response = await fetch('/war/playerDecks')

    if (response.ok) {
        const data = await response.json()
        await dispatch(add_to_pOne_deck(data.playerOneDeck))
        await dispatch(add_to_pTwo_deck(data.playerTwoDeck))
        return data
    }
}



export const addDecksToDatabase = (body) => async (dispatch) => {

    const response = await fetch('/war/start', {
        method: "POST",
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body)
    })

    if (response.ok) {
        const data = await response.json();
        await dispatch(add_to_pOne_deck(data.playerOneDeck))
        await dispatch(add_to_pTwo_deck(data.playerTwoDeck))
        return data
    }

}

export const distributeToPlayerOneDeck = (body) => async (dispatch) => {
    const response = await fetch('/war/distributeCards', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body)
    })

    if (response.ok) {
        const data = await response.json()
        await dispatch(new_player_one_deck(data.playerOneDeck))
        return data
    }


}

export const distributeToPlayerTwoDeck = (body) => async (dispatch) => {
    const response = await fetch('/war/distributeCards', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body)
    })

    if (response.ok) {
        const data = await response.json()
        await dispatch(new_player_two_deck(data.playerOneDeck))
        return data
    }

}

export const addToPot = (body) => async (dispatch) => {
    const response = await fetch('/war/pot', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body)
    })

    if (response.ok) {
        const data = await response.json()
        await dispatch(new_player_two_deck(data.playerOneDeck))
        return data
    }

}


const initialState = { decks: null }

const playerDeckReducer = (state = initialState, action) => {
    let newState;
    switch (action.type) {
        case ADD_CARDS_TO_PONE_DECKS:
            newState = Object.assign({}, state);
            newState.playerOneDeck = action.payload
            return newState;
        case ADD_CARDS_TO_PTWO_DECKS:
            newState = Object.assign({}, state);
            newState.playerTwoDeck = action.payload
            return newState;
        default:
            return state;
    }
}

export default playerDeckReducer;
